mapper.select:
SELECT t.id, t.sql_id AS sqlId, t.data_view_name AS dataViewName, t.remark, t.OPTIONS, t.FIELDS, t.buttons, t.tree_options AS treeOptions, t.data_filters AS dataFilters, t.version, t.create_time AS createTime, t.create_user AS createUser, t.last_update_time, t.last_update_user FROM t_sm_dataview t
1 = 1
and t.sql_id=#{params.sqlId}
and t.data_view_name=#{params.dataViewName}
order by t.create_time desc
